import React, { useEffect, useState } from "react";
import Board from "./Board";
import Cookies from "universal-cookie";
import {
  useChatContext,
  Channel,
  VirtualizedMessageList,
  MessageInput,
} from "stream-chat-react";
import Game from "./Game";
import "../Chat.css";

function JoinGame({ removeAllCookies, setIsAuth }) {
  const [startGame, setStartGame] = useState(false);
  const [user2, setUser2] = useState(false);
  const [channelName, setChannelName] = useState("");

  const cookies = new Cookies();

  const { client } = useChatContext();

  const [channel, setChannel] = useState(
    client.channel("messaging", cookies.get("channelName"))
  );

  const deleteChannel = async () => {
    const destroy = await channel.delete();
  };
  const createChannel = async () => {
    try {
      const response = await client.queryUsers({
        name: { $eq: user2 },
      });

      if (response.users.length === 0) {
        alert("User not found");
        return;
      }

      const newChannel = await client.channel("messaging", {
        members: [client.userID, response.users[0].id],
      });
      cookies.set("channelName", channelName);
      await newChannel.watch();

      setChannel(newChannel);
      setStartGame(true);
    } catch (error) {
      console.log(error);
    }
  };

  // const handleStartGame = async () => {
  //   if (channel) {
  //     await channel.sendEvent({
  //       type: "send-message",
  //       text: "Bruhhhhhhh",
  //     });
  //   }
  //   // setStartGame(true);
  // };

  // if (channel) {

  // useEffect(() => {
  //   const initializeChannel = async () => {
  //     // if (channel) {
  //     const channel = client.channel("messaging", channelName);
  //     await channel.watch();
  //   };
  //   initializeChannel();
  // }, []);

  const CustomInput = (
    <div>
      <ImageDropzone>
        <QuotedMessagePreview />
        <UploadsPreview />
        <EmojiPicker />
        <ChatAutoComplete />
        <FileUploadButton />
        <SendButton />
      </ImageDropzone>
    </div>
  );
  return (
    <div>
      {startGame ? (
        <Channel channel={channel} Input={CustomInput}>
          <Game channel={channel} />
        </Channel>
      ) : (
        <>
          <div>
            <h4> Create A Game</h4>
            <input
              placeholder="Username of your rival..."
              onChange={(event) => {
                setUser2(event.target.value);
              }}
            />

            <button onClick={createChannel}>Start/Join Game</button>
          </div>

          <button
            onClick={() => {
              client.disconnectUser();
              removeAllCookies();
              setIsAuth(false);
            }}
          >
            Log Out
          </button>
        </>
      )}
    </div>
  );
}

export default JoinGame;
