import React, { useEffect, useState } from "react";
import Board from "./Board";
import Cookies from "universal-cookie";

function Game({ client, removeAllCookies }) {
  const [startGame, setStartGame] = useState(false);
  const [user2, setUser2] = useState(false);
  const [channelName, setChannelName] = useState("");
  const cookies = new Cookies();

  const [channel, setChannel] = useState(
    client.channel("messaging", cookies.get("channelName"))
  );

  const deleteChannel = async () => {
    const destroy = await channel.delete();
  };
  const createChannel = async () => {
    try {
      const response = await client.queryUsers({
        username: { $eq: user2 },
      });

      if (response.users.length === 0) {
        alert("User not found");
        return;
      }

      const newChannel = await client.channel("messaging", channelName, {
        name: channelName,
        members: [cookies.get("userId"), response.users[0].id],
      });
      cookies.set("channelName", channelName);
      await newChannel.watch();
      setChannel(newChannel);
    } catch (error) {
      console.log(error);
    }
  };

  const handleStartGame = async () => {
    if (channel) {
      await channel.sendEvent({
        type: "friendship_request",
        text: "Bruhhhhhhh",
      });
    }
    // setStartGame(true);
  };

  useEffect(() => {
    if (channel) {
      channel.on((event) => {
        console.log("event", event);
        console.log("channel.state", channel.state);
      });
    }
  }, [channel]);
  return (
    <div>
      {startGame ? (
        <Board />
      ) : (
        <>
          <div>
            <h4> Create A Game</h4>
            <input
              placeholder="Username of your rival..."
              onChange={(event) => {
                setUser2(event.target.value);
              }}
            />
            <input
              placeholder="Choose a name for your game..."
              onChange={(event) => {
                setChannelName(event.target.value);
              }}
            />
            <button onClick={createChannel}>Start Game</button>
          </div>
          <div>
            <h4> Join A Game</h4>
            <button onClick={handleStartGame}>Start Game</button>
          </div>
          <button onClick={deleteChannel}>Delete Channel</button>
          <button
            onClick={() => {
              client.disconnectUser();
              removeAllCookies();
            }}
          >
            Log Out
          </button>
        </>
      )}
    </div>
  );
}

export default Game;
