  //   const initializeChannel = async () => {
  //     // if (channel) {
  //     const channel = client.channel("messaging", channelName);
  //     await channel.watch();
  //   };
  //   initializeChannel();
  // }, []);
  return (
    <div>
      {startGame ? (
        <Board channel={channel} client={client} />
      ) : (
        <>
          <div>
            <h4> Create A Game</h4>
            <input
              placeholder="Username of your rival..."
              onChange={(event) => {
                setUser2(event.target.value);
              }}
            />

            <button onClick={createChannel}>Start/Join Game</button>
          </div>

          <button
            onClick={() => {
              client.disconnectUser();
              removeAllCookies();
              setIsAuth(false);
            }}
          >
            Log Out
          </button>
        </>
      )}
    </div>
  );
}

export default Game;
