import React from "react";
import Axios from "axios";
import Cookies from "universal-cookie";

function SignUp({ setUser, user }) {
  const cookies = new Cookies();

  const signUp = () => {
    Axios.post("http://localhost:3001/auth/signup", user).then((res) => {
      const { firstName, lastName, token, userId, hashedPassword } = res.data;
      cookies.set("token", token);
      cookies.set("userId", userId);
      cookies.set("firstName", firstName);
      cookies.set("lastName", lastName);
      cookies.set("hashedPassword", hashedPassword);
    });
  };
  return (
    <div>
      <input
        placeholder="First Name"
        onChange={(event) => {
          setUser((user) => ({ ...user, firstName: event.target.value }));
        }}
      />
      <input
        placeholder="Last Name"
        onChange={(event) => {
          setUser((user) => ({ ...user, lastName: event.target.value }));
        }}
      />
      <input
        placeholder="Username"
        onChange={(event) => {
          setUser((user) => ({ ...user, username: event.target.value }));
        }}
      />
      <input
        placeholder="Password"
        type="password"
        onChange={(event) => {
          setUser((user) => ({ ...user, password: event.target.value }));
        }}
      />
      <button onClick={signUp}> Sign Up</button>
    </div>
  );
}

export default SignUp;
