import React, { useState, useEffect } from "react";
import Square from "./Square";
import { Patterns } from "../Patterns";

function Board({ channel }) {
  const [playersJoined, setPlayersJoined] = useState(false);
  const [board, setBoard] = useState(["", "", "", "", "", "", "", "", ""]);
  const [player, setPlayer] = useState("O");
  const [result, setResult] = useState({ winner: "none", state: "none" });

  useEffect(() => {
    checkWin();
    checkIfTie();

    if (player == "X") {
      setPlayer("O");
    } else {
      setPlayer("X");
    }
  }, [board]);

  useEffect(() => {
    if (result.state != "none") {
      alert(`Game Finished! Winning Player: ${result.winner}`);
      //   restartGame();
    }
  }, [result]);

  const chooseSquare = async (square) => {
    if (board[square] === "") {
      await channel.sendEvent({
        type: "game-move",
        text: { square, player: "DD" },
      });
      setBoard(
        board.map((val, idx) => {
          if (idx == square && val == "") {
            return player;
          }
          return val;
        })
      );
    }
  };

  const checkWin = () => {
    Patterns.forEach((currPattern) => {
      const firstPlayer = board[currPattern[0]];
      if (firstPlayer == "") return;
      let foundWinningPattern = true;
      currPattern.forEach((idx) => {
        if (board[idx] != firstPlayer) {
          foundWinningPattern = false;
        }
      });

      if (foundWinningPattern) {
        setResult({ winner: player, state: "Won" });
      }
    });
  };

  const checkIfTie = () => {
    let filled = true;
    board.forEach((square) => {
      if (square == "") {
        filled = false;
      }
    });

    if (filled) {
      setResult({ winner: "No One", state: "Tie" });
    }
  };

  channel.on("user.watching.start", (event) => {
    console.log("user.watching.start", event);
    setPlayersJoined(event.watcher_count === 2);
  });

  // useEffect(() => {
  //   channel.on((event) => {
  //     if (event.text.player !== client.userID) {
  //       setBoard(
  //         board.map((val, idx) => {
  //           if (idx == event.text.square && val == "") {
  //             return player == "X" ?? "O";
  //           }
  //           return val;
  //         })
  //       );
  //     }
  //   });
  // }, [channel]);

  if (!playersJoined) {
    return <div>Waiting for other player to join...</div>;
  }

  return (
    <div className="board">
      <div className="row">
        <Square
          val={board[0]}
          chooseSquare={() => {
            chooseSquare(0);
          }}
        />
        <Square
          val={board[1]}
          chooseSquare={() => {
            chooseSquare(1);
          }}
        />
        <Square
          val={board[2]}
          chooseSquare={() => {
            chooseSquare(2);
          }}
        />
      </div>
      <div className="row">
        <Square
          val={board[3]}
          chooseSquare={() => {
            chooseSquare(3);
          }}
        />
        <Square
          val={board[4]}
          chooseSquare={() => {
            chooseSquare(4);
          }}
        />
        <Square
          val={board[5]}
          chooseSquare={() => {
            chooseSquare(5);
          }}
        />
      </div>
      <div className="row">
        <Square
          val={board[6]}
          chooseSquare={() => {
            chooseSquare(6);
          }}
        />
        <Square
          val={board[7]}
          chooseSquare={() => {
            chooseSquare(7);
          }}
        />
        <Square
          val={board[8]}
          chooseSquare={() => {
            chooseSquare(8);
          }}
        />
      </div>
    </div>
  );
}

export default Board;
